import Nav from "../Nav";
import Helmet from "react-helmet";
import React from "react";
import Canvas from "./Canvas";

let canvasElem = document.querySelector("canvas");
function getMousePosition(canvas, event) {
  let rect = canvas.getBoundingClientRect();
  let x = event.clientX - rect.left;
  let y = event.clientY - rect.top;
  console.log("Coordinate x: " + x, "Coordinate y: " + y);
}
canvasElem.addEventListener("mousedown", function (e) {
  getMousePosition(canvasElem, e);
});

var box_height = 15;
var box_width = 15;
class Rectangle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
  }
  get cords() {
    return [this.x, this.y, this.x + box_width, this.y + box_height];
  }
}
var cur_rects = createRects();

export default function Portfolio() {
  const draw = (ctx, frameCount, canvas) => {
    canvas.addEventListener("mousedown", function (e) {
      console.log("");
    });
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    drawRects(ctx, cur_rects);
  };
  return (
    <div>
      <Nav />
      <Canvas draw={draw} />
    </div>
  );
}

function createRects() {
  var rects = [];
  for (var row = 0; row < 10; row++) {
    var rects_add = [];
    for (var spot = 0; spot < 10; spot++) {
      rects_add.push(new Rectangle(30 * spot, 30 * row));
    }
    rects.push(rects_add);
  }
  return rects;
}

function drawRects(ctx, cur_rects) {
  for (const i of cur_rects) {
    for (const j of i) {
      ctx.fillStyle = "white";
      ctx.beginPath();
      ctx.rect(j.x, j.y, 15, 15);
      ctx.fill();
    }
  }
}
