import Nav from "../Nav";
import Helmet from "react-helmet";
import React from "react";
//import Canvas from "./Canvas";

var box_height = 15;
var box_width = 15;
class Rectangle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.bottomrightx = this.x + box_width;
    this.bottomrighty = this.y + box_height;
    this.color = "white";
  }
  cords() {
    return [this.x, this.y, this.bottomrightx, this.bottomrighty];
  }
  getColor() {
    return this.color;
  }
  setColor(col) {
    this.color = col;
  }
}
var ctx = undefined;
var cur_rects = createRects();

class Canvas extends React.Component {
  componentDidMount() {
    const canvas = this.refs.canvas;
    ctx = canvas.getContext("2d");
    canvas.addEventListener(
      "click",
      function (e) {
        console.log(e.pageX, e.pageY);
        handleClick(e.pageX, e.pageY, cur_rects);
      },
      false
    );
    window.requestAnimationFrame(draw);
  }
  render() {
    return (
      <div>
        <canvas id="canvas" ref="canvas" width={640} height={425} />
      </div>
    );
  }
}
function draw() {
  drawRects(ctx, cur_rects);
  window.requestAnimationFrame(draw);
}
function go() {
  for (var i = 0; i < cur_rects.length; i++) {
    for (var j = 0; j < cur_rects[i].length; j++) {
      if (cur_rects[i][j].color == "red") {
        console.log(cur_rects[i][j].color);
        if (j + 1 < cur_rects[i].length) cur_rects[i][j + 1].setColor("green");
      }
    }
  }
  for (var i = 0; i < cur_rects.length; i++) {
    for (var j = 0; j < cur_rects[i].length; j++) {
      if (cur_rects[i][j].color == "green") {
        console.log(cur_rects[i][j].color);
        cur_rects[i][j].setColor("red");
      }
    }
  }
}

function createRects() {
  var rects = [];
  for (var row = 0; row < 10; row++) {
    var rects_add = [];
    for (var spot = 0; spot < 10; spot++) {
      rects_add.push(new Rectangle(30 * spot, 30 * row));
    }
    rects.push(rects_add);
  }
  return rects;
}

function drawRects() {
  for (const i of cur_rects) {
    for (const j of i) {
      ctx.fillStyle = j.color;
      // console.log("");
      ctx.beginPath();
      ctx.rect(j.x, j.y, box_width, box_height);
      ctx.fill();
    }
  }
}

function handleClick(x, y, rects) {
  for (const i of rects) {
    for (const j of i) {
      var [topleftx, toplefty, bottomrightx, bottomrighty] = j.cords();

      if (
        x > topleftx &&
        x < bottomrightx &&
        y < bottomrighty &&
        y > toplefty
      ) {
        j.setColor("red");
        console.log("j", j);
      } else {
      }
    }
  }
}

export default function Portfolio() {
  return (
    <div>
      <Nav />

      <Canvas />
      <button onClick={go}></button>
    </div>
  );
}
