import Nav from "../Nav";
import Helmet from "react-helmet";
import React from "react";
import Canvas from "./Canvas";

var box_height = 15;
var box_width = 15;
class Rectangle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
  }
  get cords() {
    return [this.x, this.y, this.x + box_width, this.y + box_height];
  }
}
var cur_rects = createRects();

export default function Portfolio() {
  const draw = (ctx, frameCount) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    drawRects(ctx, cur_rects);
  };
  return (
    <div>
      <Nav />
      <Canvas draw={draw} />
    </div>
  );
}

function createRects() {
  // Listen for mouse moves
  canvas.addEventListener("mousemove", function (event) {
    // Check whether point is inside circle
    if (ctx.isPointInPath(circle, event.offsetX, event.offsetY)) {
      ctx.fillStyle = "green";
    } else {
      ctx.fillStyle = "red";
    }

    // Draw circle
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fill(circle);
  });
  var rects = [];
  for (var row = 0; row < 10; row++) {
    var rects_add = [];
    for (var spot = 0; spot < 10; spot++) {
      rects_add.push(new Rectangle(30 * spot, 30 * row));
    }
    rects.push(rects_add);
  }
  console.log("rects", rects);
  return rects;
}

function drawRects(ctx, cur_rects) {
  console.log(cur_rects);
  for (const i of cur_rects) {
    for (const j of i) {
      console.log("here");
      ctx.fillStyle = "white";
      ctx.beginPath();
      ctx.rect(j.x, j.y, 15, 15);
      ctx.fill();
    }
  }
}
