import React, { useRef, useEffect } from "react";
import useCanvas from "../hooks/useCanvas";

const Canvas = (props) => {
  const { draw, ...rest } = props;
  const canvasRef = useCanvas(draw);

  return (
    <canvas
      ref={canvasRef}
      {...rest}
      style={{ width: "1000px", height: "1000px", margin: "100px" }}
    />
  );
};

export default Canvas;
