import Nav from "../Nav";
import Helmet from "react-helmet";
import React from "react";
//import Canvas from "./Canvas";

var box_height = 15;
var box_width = 15;
class Rectangle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
  }
  get cords() {
    return [this.x, this.y, this.x + box_width, this.y + box_height];
  }
}
var cur_rects = createRects();

class Canvas extends React.Component {
  componentDidMount() {
    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");
    const img = this.refs.image;
    img.onload = () => {
      ctx.drawImage(img, 0, 0);
      ctx.font = "40px Courier";
      ctx.fillText(this.props.text, 210, 75);
      drawRects(ctx, cur_rects);
    };
  }
  render() {
    return (
      <div>
        <canvas ref="canvas" width={640} height={425} />
        <img ref="image" src={cheese} className="hidden" />
      </div>
    );
  }
}

function createRects() {
  var rects = [];
  for (var row = 0; row < 10; row++) {
    var rects_add = [];
    for (var spot = 0; spot < 10; spot++) {
      rects_add.push(new Rectangle(30 * spot, 30 * row));
    }
    rects.push(rects_add);
  }
  return rects;
}

function drawRects(ctx, cur_rects) {
  for (const i of cur_rects) {
    for (const j of i) {
      ctx.fillStyle = "white";
      ctx.beginPath();
      ctx.rect(j.x, j.y, 15, 15);
      ctx.fill();
    }
  }
}

export default function Portfolio() {
