import Nav from "../Nav";
import Helmet from "react-helmet";
import React from "react";
//import Canvas from "./Canvas";

var box_height = 15;
var box_width = 15;
class Rectangle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.bottom = this.x + box_width;
    this.top = this.y + box_height;
  }
  get cords() {
    return [this.x, this.y, this.bottom, this.top];
  }
}
var cur_rects = createRects();

class Canvas extends React.Component {
  componentDidMount() {
    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");
    canvas.addEventListener(
      "click",
      function (e) {
        console.log(e.pageX, e.pageY);
        handleClick(e.pageX, e.pageY, cur_rects);
      },
      false
    );
    drawRects(ctx, cur_rects);
  }
  render() {
    return (
      <div>
        <canvas id="canvas" ref="canvas" width={640} height={425} />
      </div>
    );
  }
}

function createRects() {
  var rects = [];
  for (var row = 0; row < 10; row++) {
    var rects_add = [];
    for (var spot = 0; spot < 10; spot++) {
      rects_add.push(new Rectangle(30 * spot, 30 * row));
    }
    rects.push(rects_add);
  }
  return rects;
}

function drawRects(ctx, cur_rects) {
  for (const i of cur_rects) {
    for (const j of i) {
      ctx.fillStyle = "white";
      ctx.beginPath();
      ctx.rect(j.x, j.y, 15, 15);
      ctx.fill();
    }
  }
}

function handleClick(x, y, rects) {
  for (const i of rects) {
    var [topleft, topright, bottomleft, bottomright] = i.cords();
    if (x > bottomleft && x < bottomright && y > bottomleft && y < topright) {
      console.log(i);
    }
  }
}

export default function Portfolio() {
  return (
    <div>
      <Nav />
      <Canvas />
    </div>
  );
}
